mapper.insert:
 INSERT INTO TB_USER 
      ( USER_ID
      , USER_NAME
      , USER_ROLE
      , USER_GRADE
      , USER_LOCATION )
VALUES 
      ( #{userId}
      , #{userName}
      , #{userRole}
      , #{userGrade}
      , #{userLocation} )

mapper.update:
UPDATE TB_USER
   SET USER_NAME     = #{userName}
     , USER_ROLE     = #{userRole}
     , USER_GRADE    = #{userGrade}
     , USER_LOCATION = #{userLocation}
 WHERE 1 = 1
   AND USER_ID       = #{userId}
